import React, {PureComponent} from 'react';
import {
    WriterWrapper,
    WriterTitle,
    WriterSwitch,
    WriterList,
    WriterItem,
    Follow
} from '../style';

class Writer extends PureComponent {
    render(){
        return(
            <WriterWrapper>
            <WriterTitle>
                推荐作者  
                <WriterSwitch>
                <span className="iconfont">&#xe601;</span>
                    换一批
                </WriterSwitch>
            </WriterTitle>
            <WriterList>
               <WriterItem>
                <img
                alt=''
                src='//upload.jianshu.io/users/upload_avatars/10849033/7278547c-f8ba-4a5a-a2ad-5dfd43abab81.jpg?imageMogr2/auto-orient/strip|imageView2/1/w/96/h/96/format/webp'
                ></img>
                <div className='writer'>lostdays</div><Follow><span>+</span>关注</Follow>
                <p>写了231.7k字 · 2.1k喜欢</p>
                </WriterItem> 
            </WriterList>  
                Writer~
            </WriterWrapper>
        )
    }
}

export default Writer;
