    render(){
        // console.log(this.props)
        return(
            <WriterWrapper>
            <WriterTitle>
                推荐作者  
                <WriterSwitch
                onClick ={this.props.handleChangePage}
                >
                <span className="iconfont">&#xe601;</span>
                    换一批
                </WriterSwitch>
            </WriterTitle>
            <WriterList>
                {
                   this.getListArea()

                }
